package operators;

public class ArithmeticEg {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int n1=20;
		int n2=3;
		int sum=n1+n2;
		System.out.println("Sum result"+sum);
		int sub=n1-n2;
		System.out.println("Substration result"+sub);
		int mul=n1*n2;
		System.out.println("Multiplication result"+mul);
		int div=n1/n2;
		System.out.println("Division result"+div);
		int mod=n1%n2;
		System.out.println("Remainder result"+mod);
	}

}
